          <a
            className="App-link"
            //href="https://reactjs.org"
            href="https://www.google.com"
            target="_blank"
            rel="noopener noreferrer"
          >
            Learn React
          </a>
        </div>
        <div className="App-main">
          <Bloodreq />

        </div>
      </div>
    );
  }*/

  render() {
    return(
      <div className="App">
        <div className="App-header">
          <img src={logo} className="App-logo" alt="logo" />
          <h2>Welcome to React</h2>
        </div>
        <div className="App-main">
          <div className= "row">
            {this.bloodbankList()}
          </div>
          <Bloodbank/>
        </div>
      </div>
    );
  }
}




