  constructor(props){
    super(props);

    this.state = {my_bloodbank:[], my_blood_reqs:[]};

    //Bind Functions
    this.loadBloodReqs = this.loadBloodReqs.bind(this);
    this.loadBloodReqs();

    this.loadBloodBank= this.loadBloodBank.bind(this);

    this.bloodbankList = this.bloodbankList.bind(this);


    this.loadBloodBank();
    this.bloodbankList();
  }

  loadBloodReqs = () => {
    var self = this;
