  loadBloodBank = () => {
    var self = this;
    http.getBloodBank().then(data => {
      self.setState({bloodbank: data})
    }, err=> {

    });
  }


  bloodbankList = () => {
    const list = this.state.my_bloodbank.map((bloodbank) => 
      <div className="col-sm-4" key={bloodbank._id}>
       console.log(bloodbank.ffp_0pos)
