import React, { Component } from 'react';
import logo from './logo.svg';
import './App.css';
import HttpService from '../services/http-service';
import Bloodreq from '../bloodreq/bloodreq';
import Bloodbank from '../bloodbank/bloodbank';

const http = new HttpService();

class App extends Component {

  constructor(props) {
    super(props);

    this.state = { bloodbank: [], bloodreq: [] };



    //Bind Functions
    this.loadBloodReqs = this.loadBloodReqs.bind(this);
    this.loadBloodReqs();

    this.loadBloodBank = this.loadBloodBank.bind(this);
    this.loadBloodBank();

    var self = this;

    setTimeout(function afterTwoSeconds() {
      self.bloodreqList = self.bloodreqList.bind(self);
      self.bloodbankList = self.bloodbankList.bind(self);
      console.log(self.state);
    }, 1000)





  }

  loadBloodReqs = () => {
    var self = this;
    http.getBloodReqs().then(bloodreqs => {
      self.setState({ bloodreq: bloodreqs })
    }, err => {

    });
  }

  loadBloodBank = () => {
    var self = this;
    http.getBloodBank().then(data => {
      self.setState({ bloodbank: data })
    }, err => {

    });
  }


  bloodbankList = () => {
    const list = this.state.bloodbank.map((bloodbank) =>
      <div className="col-sm" key={bloodbank._id}>
        <Bloodbank
          ffp_0pos={bloodbank.ffp_0pos} ffp_0neg={bloodbank.ffp_0neg} ffp_Apos={bloodbank.ffp_Apos} ffp_Aneg={bloodbank.ffp_Aneg}
          ffp_Bpos={bloodbank.ffp_Bpos} ffp_Bneg={bloodbank.ffp_Bneg} ffp_ABpos={bloodbank.ffp_ABpos} ffp_ABneg={bloodbank.ffp_ABneg}

          pc_0pos={bloodbank.pc_0pos} pc_0neg={bloodbank.pc_0neg} pc_Apos={bloodbank.pc_Apos} pc_Aneg={bloodbank.pc_Aneg}
          pc_Bpos={bloodbank.pc_Bpos} pc_Bneg={bloodbank.pc_Bneg} pc_ABpos={bloodbank.pc_ABpos} pc_ABneg={bloodbank.pc_ABneg}
        />
      </div>
    );

    return (list);
  }


  bloodreqList = () => {
    const list = this.state.bloodreq.map((bloodreq) =>{
      var mybloodtype = "Not Specified";
      switch(bloodreq.blood_type){
        case "ffp_0pos":
            mybloodtype = "FFP 0 Rh positive (0+)";
            break;
        case "ffp_0neg":
            mybloodtype = "FFP 0 Rh negative (0-)";
            break;
        case "ffp_Apos":
            mybloodtype = "FFP A Rh positive (A+)";
            break;
        case "ffp_Aneg":
            mybloodtype = "FFP A Rh negative (A-)";
            break;
        case "ffp_Bpos":
            mybloodtype = "FFP B Rh positive (B+)";
            break;
        case "ffp_Bneg":
            mybloodtype = "FFP B Rh negative (AB-)";
            break;
        case "ffp_ABpos":
            mybloodtype = "FFP AB Rh positive (AB+)";
            break;
        case "ffp_ABneg":
            mybloodtype = "FFP AB Rh negative (AB-)"; 
            break;            
        case "pc_0pos":
            mybloodtype = "PC 0 Rh positive (0+)";
            break;
        case "pc_0neg":
            mybloodtype = "PC 0 Rh negative (0-)";
            break;
        case "pc_Apos":
            mybloodtype = "PC A Rh positive (A+)";
            break;
        case "pc_Aneg":
            mybloodtype = "PC A Rh negative (A-)";
            break;
        case "pc_Bpos":
            mybloodtype = "PC B Rh positive (B+)";
            break;
        case "pc_Bneg":
            mybloodtype = "PC B Rh negative (AB-)";
            break;
        case "pc_ABpos":
            mybloodtype = "PC AB Rh positive (AB+)";
            break;
        case "pc_ABneg":
            mybloodtype = "PC AB Rh negative (AB-)";
            break;
       }
    <div className="col-sm" key={bloodreq._id}>

      <Bloodreq 





      />
    </div>


    }



    );

    return (list);
  }





